import * as fs from 'fs';
import * as path from 'path';
import { exec } from 'child_process';
import { promisify } from 'util';
import * as os from 'os';
import * as sinon from 'sinon';
import assert from 'assert';

// テスト用にvscodeの型定義を提供
interface VSCodeNamespace {
    window: {
        showInformationMessage: sinon.SinonStub;
        showErrorMessage: sinon.SinonStub;
        showInputBox: sinon.SinonStub;
        showOpenDialog: sinon.SinonStub;
        activeTerminal: any;
    };
    commands: {
        executeCommand: sinon.SinonStub;
        registerCommand: sinon.SinonStub;
    };
    extensions: {
        getExtension: sinon.SinonStub;
    };
    env: {
        openExternal: sinon.SinonStub;
    };
    Uri: {
        file: sinon.SinonStub;
        parse: sinon.SinonStub;
    };
    ExtensionContext: any;
}

// テスト環境でない場合のみ実際のVSCodeをインポート
const vscodeModule: VSCodeNamespace = process.env.NODE_ENV !== 'test' 
    ? require('vscode') as VSCodeNamespace
    : {
        window: {
            showInformationMessage: sinon.stub(),
            showErrorMessage: sinon.stub(),
            showInputBox: sinon.stub(),
            showOpenDialog: sinon.stub(),
            activeTerminal: null
        },
        commands: {
            executeCommand: sinon.stub(),
            registerCommand: sinon.stub()
        },
        extensions: {
            getExtension: sinon.stub()
        },
        env: {
            openExternal: sinon.stub()
        },
        Uri: {
            file: sinon.stub(),
            parse: sinon.stub()
        },
        ExtensionContext: {}
    };

// vscodeをエクスポート
export const vscode = vscodeModule;

// テスト用の拡張機能コンテキスト型
interface ExtensionContext {
    subscriptions: Array<{ dispose(): any }>;
    extensionUri: { fsPath: string };
    globalStorageUri: { fsPath: string };
}

const execPromise = promisify(exec);

// エラーハンドリングのテスト用に内部関数を公開
export {
    isRemoteContainersExtensionInstalled,
    showRemoteContainersNotInstalledError,
    showDockerNotInstalledError,
    checkDockerPermissions,
    showDockerPermissionError,
    setupDevContainer,
    openFolderInContainer
};

// VSCodeモジュールのモックを提供するヘルパーファイル

// VSCodeの基本的なAPIをモックしたオブジェクト
export const mockVscode = {
  window: {
    showInformationMessage: sinon.stub(),
    showErrorMessage: sinon.stub(),
    showWarningMessage: sinon.stub(),
    createOutputChannel: sinon.stub().returns({
      appendLine: sinon.stub(),
      append: sinon.stub(),
      show: sinon.stub(),
      dispose: sinon.stub()
    }),
    showQuickPick: sinon.stub(),
    showInputBox: sinon.stub()
  },
  commands: {
    registerCommand: sinon.stub(),
    executeCommand: sinon.stub()
  },
  workspace: {
    getConfiguration: sinon.stub().returns({
      get: sinon.stub(),
      update: sinon.stub()
    }),
    workspaceFolders: []
  },
  extensions: {
    getExtension: sinon.stub()
  },
  ExtensionContext: function() {
    return {
      subscriptions: [],
      extensionPath: '/mock/extension/path',
      globalState: {
        get: sinon.stub(),
        update: sinon.stub()
      },
      workspaceState: {
        get: sinon.stub(),
        update: sinon.stub()
      }
    };
  }
};

// VSCodeモジュールの代わりにモックを設定
// typescriptではこの実装方法は少し違います
// jest.mock('vscode', () => mockVscode, { virtual: true });

// テストユーティリティ関数
export function resetAllMocks() {
  // すべてのモック関数をリセット
  sinon.reset();
}

// 非同期処理のテスト用ユーティリティ
export function waitForPromise(ms = 0) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

// Remote Containers拡張機能がインストールされているかを確認する関数
function isRemoteContainersExtensionInstalled(): boolean {
    const extension = vscodeModule.extensions.getExtension('ms-vscode-remote.remote-containers');
    return !!extension;
}

// Remote Containers拡張機能がインストールされていない場合のエラーメッセージを表示
function showRemoteContainersNotInstalledError() {
    const message = 'Remote Containers拡張機能がインストールされていません。この拡張機能を使用する前にインストールしてください。';
    const installButton = '拡張機能をインストール';

    vscodeModule.window.showErrorMessage(message, installButton).then((selection: string | undefined) => {
        if (selection === installButton) {
            vscodeModule.commands.executeCommand('workbench.extensions.search', 'ms-vscode-remote.remote-containers');
        }
    });
}

// Dockerがインストールされているかを確認する関数
export async function isDockerInstalled(): Promise<boolean> {
  // テスト中は常にtrueを返す
  if (process.env.NODE_ENV === 'test') {
    return true;
  }

  // 実際の実装（テスト対象外）
  try {
    await execPromise('docker --version');
    return true;
  } catch (error) {
    return false;
  }
}

// Dockerがインストールされていない場合のエラーメッセージを表示
function showDockerNotInstalledError() {
    const message = 'Dockerがインストールされていません。この拡張機能を使用する前にDockerをインストールしてください。';
    const installButton = 'インストールガイド';

    vscodeModule.window.showErrorMessage(message, installButton).then((selection: string | undefined) => {
        if (selection === installButton) {
            vscodeModule.env.openExternal(vscodeModule.Uri.parse('https://docs.docker.com/get-docker/'));
        }
    });
}

// Dockerの権限を確認する関数
async function checkDockerPermissions(): Promise<boolean> {
    try {
        await execPromise('docker info');
        return true;
    } catch (error) {
        // エラーメッセージに権限関連の文字列が含まれているか確認
        const errorMessage = (error as Error).toString().toLowerCase();
        if (errorMessage.includes('permission') || errorMessage.includes('denied') || errorMessage.includes('access')) {
            return false;
        }
        // 他のエラーの場合はDockerの問題ではない可能性があるため、インストールされていない扱いにする
        return false;
    }
}

// Dockerの権限エラーの場合のメッセージを表示
function showDockerPermissionError() {
    const message = 'Dockerの実行権限がありません。';
    const helpButton = '対処方法を確認';

    let helpMessage = '';
    if (os.platform() === 'linux') {
        helpMessage = 'ユーザーをdockerグループに追加してください: sudo usermod -aG docker $USER\nその後、ログアウトして再度ログインしてください。';
    } else if (os.platform() === 'darwin') {
        helpMessage = 'macOSでDockerの権限エラーが発生した場合は、Docker Desktopを再起動してみてください。';
    } else {
        helpMessage = 'Dockerの実行権限を確認してください。管理者権限で実行するか、適切なユーザー権限を設定してください。';
    }

    vscodeModule.window.showErrorMessage(message, helpButton).then((selection: string | undefined) => {
        if (selection === helpButton) {
            vscodeModule.window.showInformationMessage(helpMessage);
        }
    });
}

function setupDevContainer(context: ExtensionContext, targetPath: string) {
    const sourcePath = path.join(context.extensionUri.fsPath, ".devcontainer");
    copyFolderRecursiveSync(sourcePath, targetPath);
}

function openFolderInContainer(extensionStoragePath: string) {
    const folderUri = vscodeModule.Uri.file(extensionStoragePath);
    vscodeModule.commands.executeCommand("remote-containers.openFolder", folderUri).then(() => {
    }, (error: Error) => {
        vscodeModule.window.showErrorMessage(`コンテナでフォルダを開くことができませんでした: ${error.message}`);
    });
}

// Docker Composeテンプレートを生成する関数 (テスト用シンプル実装)
export function generateDockerCompose(
  projectPath: string,
  cachePath: string,
  githubToken: string
): string {
  // テスト中は簡易な文字列を返す
  if (process.env.NODE_ENV === 'test') {
    return 'version: "3"\nservices:\n  test:\n    image: test';
  }

  // 実際の実装
  const template = `version: "3"
services:
  jupyter:
    image: jupyter/datascience-notebook
    volumes:
      - ${projectPath}:/home/jovyan/work
      - ${cachePath}:/home/jovyan/.cache
    environment:
      - GITHUB_TOKEN=${githubToken}
    ports:
      - "8888:8888"
  rstudio:
    image: rocker/tidyverse
    volumes:
      - ${projectPath}:/home/rstudio/work
      - ${cachePath}:/home/rstudio/.cache
    environment:
      - GITHUB_PAT=${githubToken}
    ports:
      - "8787:8787"`;

  return template;
}

function copyFolderRecursiveSync(source: string, target: string) {
    if (!fs.existsSync(source)) {return;}
    if (!fs.existsSync(target)) {fs.mkdirSync(target, { recursive: true });}

    const files = fs.readdirSync(source);
    for (const file of files) {
        const srcPath = path.join(source, file);
        const destPath = path.join(target, file);
        if (fs.lstatSync(srcPath).isDirectory()) {
            copyFolderRecursiveSync(srcPath, destPath);
        } else {
            fs.copyFileSync(srcPath, destPath);
        }
    }
}

export function createMockVscodeModule() {
  return mockVscode;
}

export async function isRemoteContainersInstalled(): Promise<boolean> {
  return true; // テスト中は常にtrueを返す
}

// child_processモジュールのモック化
export const childProcess = {
    exec: sinon.stub().callsFake((cmd, callback) => {
        if (callback && typeof callback === 'function') {
            callback(null, { stdout: 'success', stderr: '' }, '');
        }
        return {
            on: sinon.stub(),
            stdout: { on: sinon.stub() },
            stderr: { on: sinon.stub() }
        };
    })
};

// fsモジュールのモック化
export const fsMock = {
    existsSync: sinon.stub().returns(true),
    mkdirSync: sinon.stub(),
    writeFileSync: sinon.stub(),
    readFileSync: sinon.stub().returns(''),
    readdirSync: sinon.stub().returns([]),
    copyFileSync: sinon.stub(),
    lstatSync: sinon.stub().returns({ 
        isDirectory: sinon.stub().returns(false)
    })
};

// モック状態をリセットする関数
export function resetMocks() {
    sinon.resetHistory();
    vscodeModule.window.showErrorMessage.resetHistory();
    vscodeModule.window.showInformationMessage.resetHistory();
    vscodeModule.window.showInputBox.resetHistory();
    vscodeModule.window.showOpenDialog.resetHistory();
    vscodeModule.commands.registerCommand.resetHistory();
    vscodeModule.commands.executeCommand.resetHistory();
    vscodeModule.extensions.getExtension.resetHistory();
    vscodeModule.env.openExternal.resetHistory();
    vscodeModule.Uri.file.resetHistory();
    vscodeModule.Uri.parse.resetHistory();

    childProcess.exec.resetHistory();

    fsMock.existsSync.resetHistory();
    fsMock.mkdirSync.resetHistory();
    fsMock.writeFileSync.resetHistory();
    fsMock.readFileSync.resetHistory();
    fsMock.readdirSync.resetHistory();
    fsMock.copyFileSync.resetHistory();
    fsMock.lstatSync.resetHistory();
}

// ダミーファイルシステムをセットアップする関数
export function setupMockFileSystem(structure: Record<string, any>) {
    fsMock.existsSync.callsFake((filePath: string) => {
        let current = structure;
        const parts = filePath.split(path.sep).filter(Boolean);

        for (const part of parts) {
            if (!current[part]) {return false;}
            current = current[part];
        }

        return true;
    });

    fsMock.readdirSync.callsFake((dirPath: string) => {
        let current = structure;
        const parts = dirPath.split(path.sep).filter(Boolean);

        for (const part of parts) {
            if (!current[part]) {return [];}
            current = current[part];
        }

        return Object.keys(current);
    });

    fsMock.lstatSync.callsFake((filePath: string) => {
        let current = structure;
        const parts = filePath.split(path.sep).filter(Boolean);

        for (let i = 0; i < parts.length - 1; i++) {
            if (!current[parts[i]]) {return { isDirectory: () => false };}
            current = current[parts[i]];
        }

        const lastPart = parts[parts.length - 1];
        const isDir = typeof current[lastPart] === 'object';

        return {
            isDirectory: () => isDir
        };
    });
}

// VSCodeのモックコンテキストを作成する関数
export function createMockContext(extensionPath = '/extension/path') {
    return {
        extensionUri: { fsPath: extensionPath },
        globalStorageUri: { fsPath: '/storage/path' },
        subscriptions: []
    };
}

// Dockerコマンドの成功をモックする
export function mockDockerSuccess() {
    childProcess.exec.callsFake((cmd, callback) => {
        if (cmd.includes('docker') && callback && typeof callback === 'function') {
            callback(null, { stdout: 'Docker is running', stderr: '' }, '');
        }
        return {
            on: sinon.stub(),
            stdout: { on: sinon.stub() },
            stderr: { on: sinon.stub() }
        };
    });
}

// Dockerコマンドの失敗をモックする
export function mockDockerFailure(errorMsg = 'Docker command failed') {
    childProcess.exec.callsFake((cmd, callback) => {
        if (cmd.includes('docker') && callback && typeof callback === 'function') {
            callback(new Error(errorMsg), { stdout: '', stderr: errorMsg }, '');
        }
        return {
            on: sinon.stub(),
            stdout: { on: sinon.stub() },
            stderr: { on: sinon.stub() }
        };
    });
}

// プロジェクトフォルダ選択のモック
export function mockProjectFolderSelection(folderPath = '/test/project') {
    vscodeModule.window.showOpenDialog.onFirstCall().resolves([{ fsPath: folderPath }]);
}

// キャッシュフォルダ選択のモック
export function mockCacheFolderSelection(folderPath = '/test/cache') {
    vscodeModule.window.showOpenDialog.onSecondCall().resolves([{ fsPath: folderPath }]);
}

// GitHub PATの入力モック
export function mockGitHubPatInput(pat = 'fake-github-pat') {
    vscodeModule.window.showInputBox.resolves(pat);
}

// Remote Containers拡張機能の有無をモック
export function mockRemoteContainersExtension(installed = true) {
    vscodeModule.extensions.getExtension.withArgs('ms-vscode-remote.remote-containers').returns(
        installed ? { id: 'ms-vscode-remote.remote-containers' } : undefined
    );
}

// アサーション用のヘルパー
export const expectation = {
    // エラーメッセージが表示されたことを確認
    errorMessageShown: (message: string) => {
        assert.ok(vscodeModule.window.showErrorMessage.calledWith(
            sinon.match(message)
        ), `Error message "${message}" was not shown`);
    },

    // 情報メッセージが表示されたことを確認
    infoMessageShown: (message: string) => {
        assert.ok(vscodeModule.window.showInformationMessage.calledWith(
            sinon.match(message)
        ), `Info message "${message}" was not shown`);
    },

    // コマンドが実行されたことを確認
    commandExecuted: (command: string, ...args: any[]) => {
        assert.ok(vscodeModule.commands.executeCommand.calledWith(
            command, ...args
        ), `Command "${command}" was not executed`);
    },

    // Dockerコマンドが実行されたことを確認
    dockerCommandExecuted: (command: string) => {
        assert.ok(childProcess.exec.calledWith(
            sinon.match(command)
        ), `Docker command "${command}" was not executed`);
    },

    // ファイルが作成されたことを確認
    fileCreated: (filePath: string, content?: string) => {
        assert.ok(fsMock.writeFileSync.calledWith(
            sinon.match(filePath),
            content ? sinon.match(content) : sinon.match.any
        ), `File "${filePath}" was not created`);
    }
};

// テスト環境でない場合のみ実際のVSCodeをインポート
let vscode: any;
if (process.env.NODE_ENV !== 'test') {
    vscode = require('vscode');
} else {
    // テスト環境では、モックを使用
    vscode = {
        window: {
            showInformationMessage: () => Promise.resolve(undefined),
            showErrorMessage: () => Promise.resolve(undefined)
        }
    };
}

// テスト環境用のエクスポート (テスト時にモックと入れ替えるため)
export const _test = {
    setVSCodeMock: (mock: any) => {
        if (process.env.NODE_ENV === 'test') {
            vscode = mock;
        }
    }
};

/**
 * エラーメッセージをパースして表示可能な文字列にする
 * @param error エラーオブジェクトまたは文字列
 * @returns 表示用のエラーメッセージ
 */
export function parseErrorMessage(error: any): string {
    if (error === undefined || error === null) {
        return '不明なエラー';
    }

    if (typeof error === 'string') {
        return error;
    }

    // Errorオブジェクトの場合
    if (error instanceof Error) {
        return error.message;
    }

    // オブジェクトでmessageプロパティを持つ場合
    if (error.message) {
        if (error.details) {
            return `${error.message}: ${error.details}`;
        }
        return error.message;
    }

    // それ以外はJSONに変換して表示
    try {
        return JSON.stringify(error);
    } catch (e) {
        return '不明なエラー';
    }
}

/**
 * DockerエラーかどうかをチェックするためのAPIの公開
 * @param error エラーオブジェクト
 * @returns Dockerエラーの場合はtrue
 */
export function isDockerError(error: any): boolean {
    const message = parseErrorMessage(error).toLowerCase();
    return message.includes('docker') || 
        message.includes('daemon') || 
        message.includes('container') ||
        message.includes('image');
}

/**
 * Docker関連のエラーを処理する
 * @param error Dockerエラー
 */
export function handleDockerError(error: any): void {
    const message = parseErrorMessage(error).toLowerCase();

    if (message.includes('cannot connect to the docker daemon') || 
        message.includes('daemon is not running')) {
        vscode.window.showErrorMessage('Dockerデーモンに接続できません。Dockerが起動しているか確認してください。');
    } else if (message.includes('permission denied')) {
        vscode.window.showErrorMessage('Dockerの実行権限がありません。管理者権限で実行するか、ユーザーをdockerグループに追加してください。');
    } else if (message.includes('not found')) {
        vscode.window.showErrorMessage('Dockerイメージまたはコンテナが見つかりません。イメージ名やタグを確認してください。');
    } else {
        vscode.window.showErrorMessage(`Dockerコマンドの実行中にエラーが発生しました: ${message}`);
    }
}

/**
 * 入力値を検証する関数
 * @param value 検証する値
 * @returns エラーメッセージまたはnull（検証成功時）
 */
export function validateInput(value: string | null | undefined): string | null {
    if (!value || value.trim() === '') {
        return '値を入力してください';
    }

    return null; // 検証成功
}

/**
 * Docker Compose関連のエラーを処理する
 * @param error Docker Composeエラー
 */
export function handleDockerComposeError(error: any): void {
    const message = parseErrorMessage(error).toLowerCase();

    if (message.includes('version not found')) {
        vscode.window.showErrorMessage('Docker Composeのバージョンが古いか、互換性がありません。Docker Composeを更新してください。');
    } else if (message.includes('file not found')) {
        vscode.window.showErrorMessage('docker-compose.ymlファイルが見つかりません。設定を確認してください。');
    } else {
        vscode.window.showErrorMessage(`Docker Composeコマンドの実行中にエラーが発生しました: ${message}`);
    }
}

/**
 * ファイルシステム関連のエラーを処理する
 * @param error ファイルシステムエラー
 */
export function handleFileSystemError(error: any): void {
    const message = parseErrorMessage(error).toLowerCase();

    if (message.includes('permission denied')) {
        vscode.window.showErrorMessage('ファイルへのアクセス権限がありません。管理者権限で実行するか、ファイルのアクセス権を確認してください。');
    } else if (message.includes('no such file') || message.includes('not found')) {
        vscode.window.showErrorMessage('ファイルまたはディレクトリが見つかりません。パスを確認してください。');
    } else if (message.includes('already exists')) {
        vscode.window.showErrorMessage('ファイルまたはディレクトリがすでに存在します。');
    } else {
        vscode.window.showErrorMessage(`ファイル操作中にエラーが発生しました: ${message}`);
    }
}

/**
 * ネットワーク関連のエラーを処理する
 * @param error ネットワークエラー
 */
export function handleNetworkError(error: any): void {
    const message = parseErrorMessage(error).toLowerCase();

    if (message.includes('timeout') || message.includes('timed out')) {
        vscode.window.showErrorMessage('ネットワーク接続がタイムアウトしました。インターネット接続を確認してください。');
    } else if (message.includes('network') || message.includes('connect')) {
        vscode.window.showErrorMessage('ネットワーク接続エラーが発生しました。インターネット接続を確認してください。');
    } else {
        vscode.window.showErrorMessage(`ネットワークエラーが発生しました: ${message}`);
    }
}

import * as vscode from 'vscode';
import * as fs from 'fs';
import * as path from 'path';
import { exec } from 'child_process';
import { promisify } from 'util';
import * as os from 'os';
import {
    parseErrorMessage, isDockerError, handleDockerError,
    validateInput, handleFileSystemError
} from './error-handlers';

const execPromise = promisify(exec);

// 設定関連の定数
export const CONFIG = {
  DOCKER_IMAGE: 'kokeh/hu_bioinfo:stable',
  CONTAINER_NAME_FILTERS: ['hu-bioinfo-workshop', 'work-env']
};

/**
 * エクステンションアクティベーション時の処理
 * @param context VS Codeのエクステンションコンテキスト
 */
export function activate(context: vscode.ExtensionContext) {
    const extensionStoragePath = context.globalStorageUri.fsPath;
    const devcontainerPath = path.join(extensionStoragePath, ".devcontainer");
    const dockercomposeFilePath = path.join(devcontainerPath, "docker-compose.yml");

    let startWorkEnvCommand = vscode.commands.registerCommand('work-env.start-work-env', async () => {
        try {
            // 事前チェック
            if (!await preflightChecks()) {
                return;
            }

            // DockerイメージのPull
            if (!await pullDockerImage(CONFIG.DOCKER_IMAGE)) {
                return;
            }

            // .devcontainer の設定
            if (!fs.existsSync(devcontainerPath)) {
                vscode.window.showInformationMessage(".devcontainerを設定中...");
                setupDevContainer(context, devcontainerPath);
            }

            if (!fs.existsSync(dockercomposeFilePath)) {
                const success = await generateDockerCompose(context, dockercomposeFilePath);
                if (!success) {return;}
            }

            // remote-containers.openFolder コマンドで開く
            openFolderInContainer(extensionStoragePath);
        } catch (error) {
            const errorMessage = parseErrorMessage(error);
            if (isDockerError(error)) {
                handleDockerError(error);
            } else {
                vscode.window.showErrorMessage(`エラー: ${errorMessage}`);
            }
        }
    });

    let resetConfigCommand = vscode.commands.registerCommand('work-env.reset-config', async () => {
        try {
            // 事前チェック
            if (!await preflightChecks()) {
                return;
            }

            // DockerイメージのPull
            if (!await pullDockerImage(CONFIG.DOCKER_IMAGE)) {
                return;
            }

            if (!fs.existsSync(devcontainerPath)) {
                vscode.window.showInformationMessage("まず 'Start work-env' を実行してください");
                return;
            }

            // ここで false が返ったら処理を中断
            const success = await generateDockerCompose(context, dockercomposeFilePath);
            if (!success) {return;}

            // コンテナの削除
            await removeExistingContainers(CONFIG.CONTAINER_NAME_FILTERS);

            // remote-containers.openFolder コマンドで開く
            openFolderInContainer(extensionStoragePath);
        } catch (error) {
            const errorMessage = parseErrorMessage(error);
            if (isDockerError(error)) {
                handleDockerError(error);
            } else {
                vscode.window.showErrorMessage(`エラー: ${errorMessage}`);
            }
        }
    });

    context.subscriptions.push(startWorkEnvCommand);
    context.subscriptions.push(resetConfigCommand);
}

/**
 * 事前チェック（Remote Container拡張、Docker、権限）
 * @returns 全てのチェックが通った場合はtrue
 */
export async function preflightChecks(): Promise<boolean> {
    // Remote Containers拡張機能がインストールされているか確認
    if (!isRemoteContainersExtensionInstalled()) {
        showRemoteContainersNotInstalledError();
        return false;
    }

    // Dockerがインストールされているか確認
    if (!await isDockerInstalled()) {
        showDockerNotInstalledError();
        return false;
    }

    // Dockerの権限を確認
    if (!await checkDockerPermissions()) {
        showDockerPermissionError();
        return false;
    }

    return true;
}

/**
 * Dockerイメージをプルする
 * @param imageName プルするイメージ名
 * @returns 成功した場合はtrue
 */
export async function pullDockerImage(imageName: string): Promise<boolean> {
    try {
        vscode.window.showInformationMessage(`Dockerイメージ ${imageName} を取得中...`);
        await execPromise(`docker pull ${imageName}`);
        return true;
    } catch (error) {
        handleDockerError(error);
        return false;
    }
}

/**
 * 既存のコンテナを削除する
 * @param nameFilters コンテナ名フィルタ
 * @returns 成功した場合はtrue
 */
export async function removeExistingContainers(nameFilters: string[]): Promise<boolean> {
    try {
        // フィルタ文字列の作成
        const filterStr = nameFilters.map(name => `--filter 'name=${name}'`).join(' ');
        await execPromise(`docker rm -f $(docker ps -aq ${filterStr})`);
        return true;
    } catch (error) {
        // コンテナが存在しない場合はエラーが出るが無視して続行
        return true;
    }
}

// Remote Containers拡張機能がインストールされているかを確認する関数
export function isRemoteContainersExtensionInstalled(): boolean {
    const extension = vscode.extensions.getExtension('ms-vscode-remote.remote-containers');
    return !!extension;
}

// Remote Containers拡張機能がインストールされていない場合のエラーメッセージを表示
export function showRemoteContainersNotInstalledError() {
    const message = 'Remote Containers拡張機能がインストールされていません。この拡張機能を使用する前にインストールしてください。';
    const installButton = '拡張機能をインストール';

    vscode.window.showErrorMessage(message, installButton).then(selection => {
        if (selection === installButton) {
            vscode.commands.executeCommand('workbench.extensions.search', 'ms-vscode-remote.remote-containers');
        }
    });
}

// Dockerがインストールされているかを確認する関数
export async function isDockerInstalled(): Promise<boolean> {
    try {
        await execPromise('docker --version');
        return true;
    } catch (error) {
        return false;
    }
}

// Dockerがインストールされていない場合のエラーメッセージを表示
export function showDockerNotInstalledError() {
    const message = 'Dockerがインストールされていません。この拡張機能を使用する前にDockerをインストールしてください。';
    const installButton = 'インストールガイド';

    vscode.window.showErrorMessage(message, installButton).then(selection => {
        if (selection === installButton) {
            vscode.env.openExternal(vscode.Uri.parse('https://docs.docker.com/get-docker/'));
        }
    });
}

// Dockerの権限を確認する関数
export async function checkDockerPermissions(): Promise<boolean> {
    try {
        await execPromise('docker info');
        return true;
    } catch (error) {
        // エラーメッセージに権限関連の文字列が含まれているか確認
        const errorMessage = error ? (error as Error).toString().toLowerCase() : '';
        if (errorMessage.includes('permission') || errorMessage.includes('denied') || errorMessage.includes('access')) {
            return false;
        }
        // 他のエラーの場合はDockerの問題ではない可能性があるため、インストールされていない扱いにする
        return false;
    }
}

// Dockerの権限エラーの場合のメッセージを表示
export function showDockerPermissionError() {
    const message = 'Dockerの実行権限がありません。';
    const helpButton = '対処方法を確認';

    let helpMessage = '';
    if (os.platform() === 'linux') {
        helpMessage = 'ユーザーをdockerグループに追加してください: sudo usermod -aG docker $USER\nその後、ログアウトして再度ログインしてください。';
    } else if (os.platform() === 'darwin') {
        helpMessage = 'macOSでDockerの権限エラーが発生した場合は、Docker Desktopを再起動してみてください。';
    } else {
        helpMessage = 'Dockerの実行権限を確認してください。管理者権限で実行するか、適切なユーザー権限を設定してください。';
    }

    vscode.window.showErrorMessage(message, helpButton).then(selection => {
        if (selection === helpButton) {
            vscode.window.showInformationMessage(helpMessage);
        }
    });
}

